mapper.select:
SELECT *
FROM player
WHERE player.lobby_id = #{lobbyId}
    AND player.username = #{username}

mapper.insert:
INSERT INTO player(
    username,
    lobby_id,
    client_id
)
VALUES (
    #{username},
    #{lobbyId},
    #{clientId}
)

mapper.delete:
DELETE FROM player
WHERE player.username = #{username}
    AND player.lobby_id = #{lobbyId}
